from Bio.Restriction import *
from Bio.Seq import Seq



def cut_positions(seq):
    rb = NcoI + SacI
    analysis = Analysis(rb, test_seq, linear=False)
    cut_map = analysis.full()

    cut_sites = []

    for re, loc in cut_map.items():
        cut_sites.extend(loc)

    cut_pos = ([0] + cut_sites + [len(seq)])

    fragments = []
    for i in range(len(cut_pos) - 1):
        start = cut_pos[i]
        end = cut_pos[i+1]
        fragments.append(seq[start:end])

    return fragments

if __name__ == '__main__':
    test_seq = Seq(
        "")
    print (len(test_seq))
    frags = cut_positions(test_seq)
    for seq in frags:
        print(len(seq))
